import {
	BaseMessageSignerWalletAdapter,
	type EventEmitter,
	scopePollingDetectionStrategy,
	type SupportedTransactionVersions,
	WalletAccountError,
	WalletConnectionError,
	WalletDisconnectedError,
	WalletDisconnectionError,
	type WalletError,
	type WalletName,
	WalletNotConnectedError,
	WalletNotReadyError,
	WalletPublicKeyError,
	WalletReadyState,
	WalletSignMessageError,
	WalletSignTransactionError,
} from '@solana/wallet-adapter-base';
import {
	PublicKey,
	type Transaction,
	type VersionedTransaction,
} from '@solana/web3.js';

interface WallessWalletEvents {
	connect(...args: unknown[]): unknown;
	disconnect(...args: unknown[]): unknown;
	accountChanged(newPublicKey: PublicKey): unknown;
}

interface WallessWallet extends EventEmitter<WallessWalletEvents> {
	isWalless?: boolean;
	publicKey?: { toBytes(): Uint8Array };
	isConnected: boolean;
	signMessageOnSolana(message: Uint8Array): Promise<{ signature: Uint8Array }>;
	signTransactionOnSolana<T extends Transaction | VersionedTransaction>(
		transaction: T,
	): Promise<T>;
	signAllTransactionsOnSolana<T extends Transaction | VersionedTransaction>(
		transactions: T[],
	): Promise<T[]>;
	connect(): Promise<void>;
	disconnect(): Promise<void>;
}

interface WallessWindow extends Window {
	walless: WallessWallet;
}

declare const window: WallessWindow;

export const WallessWalletName = 'Walless' as WalletName<'Walless'>;
export class WallessWalletAdapter extends BaseMessageSignerWalletAdapter {
	name = WallessWalletName;
	url = 'https://walless.io/';
	icon =
		'data:image/png;base64,';
	readonly supportedTransactionVersions: SupportedTransactionVersions | null =
		null;

	private _connecting: boolean;
	private _wallet: WallessWallet | null;
	private _publicKey: PublicKey | null;
	private _readyState: WalletReadyState =
		typeof window === 'undefined' || typeof document === 'undefined'
			? WalletReadyState.Unsupported
			: WalletReadyState.NotDetected;

	constructor() {
		super();
		this._connecting = false;
		this._wallet = null;
		this._publicKey = null;

		if (this._readyState !== WalletReadyState.Unsupported) {
			scopePollingDetectionStrategy(() => {
				if (window.walless?.isWalless) {
					this._readyState = WalletReadyState.Installed;
					this.emit('readyStateChange', this._readyState);
					return true;
				}
				return false;
			});
		}
	}

	get publicKey() {
		return this._publicKey;
	}

	get connecting() {
		return this._connecting;
	}

	get connected() {
		return !!this._wallet?.isConnected;
	}

	get readyState() {
		return this._readyState;
	}

	async connect(): Promise<void> {
		try {
			if (this.connected || this.connecting) return;
			if (this._readyState !== WalletReadyState.Installed)
				throw new WalletNotReadyError();

			this._connecting = true;

			const wallet = window.walless;

			try {
				await wallet?.connect();
			} catch (error: unknown) {
				const walletError = error as WalletError;
				throw new WalletConnectionError(walletError.message, walletError);
			}

			if (!wallet?.publicKey) throw new WalletAccountError();

			let publicKey: PublicKey;
			try {
				publicKey = new PublicKey(wallet.publicKey.toBytes());
			} catch (error) {
				const walletError = error as WalletError;
				throw new WalletPublicKeyError(walletError.message, walletError);
			}

			wallet.on('disconnect', this._disconnected);

			this._wallet = wallet;
			this._publicKey = publicKey;

			this.emit('connect', publicKey);
		} catch (error) {
			const walletError = error as WalletError;
			this.emit('error', walletError);
			throw error;
		} finally {
			this._connecting = false;
		}
	}

	async disconnect(): Promise<void> {
		const wallet = this._wallet;
		if (wallet) {
			wallet.off('disconnect', this._disconnected);

			this._wallet = null;
			this._publicKey = null;

			try {
				await wallet.disconnect();
			} catch (error) {
				const walletError = error as WalletError;
				this.emit(
					'error',
					new WalletDisconnectionError(walletError.message, walletError),
				);
			}
		}

		this.emit('disconnect');
	}

	async signMessage(message: Uint8Array): Promise<Uint8Array> {
		try {
			const wallet = this._wallet;
			if (!wallet) throw new WalletNotConnectedError();

			try {
				const { signature } = await wallet.signMessageOnSolana(message);
				return signature;
			} catch (error) {
				const walletError = error as WalletError;
				throw new WalletSignMessageError(walletError.message, walletError);
			}
		} catch (error) {
			const walletError = error as WalletError;
			this.emit('error', walletError);
			throw error;
		}
	}

	async signTransaction<T extends Transaction | VersionedTransaction>(
		transaction: T,
	): Promise<T> {
		try {
			const wallet = this._wallet;
			if (!wallet) throw new WalletNotConnectedError();

			try {
				return (
					(await wallet.signTransactionOnSolana(transaction)) || transaction
				);
			} catch (error: unknown) {
				const walletError = error as WalletError;
				throw new WalletSignTransactionError(walletError.message, walletError);
			}
		} catch (error: unknown) {
			const walletError = error as WalletError;
			this.emit('error', walletError);
			throw error;
		}
	}

	async signAllTransactions<T extends Transaction | VersionedTransaction>(
		transactions: T[],
	): Promise<T[]> {
		try {
			const wallet = this._wallet;
			if (!wallet) throw new WalletNotConnectedError();

			try {
				return (
					(await wallet.signAllTransactionsOnSolana(transactions)) ||
					transactions
				);
			} catch (error) {
				const walletError = error as WalletError;
				throw new WalletSignTransactionError(walletError.message, walletError);
			}
		} catch (error) {
			const walletError = error as WalletError;
			this.emit('error', walletError);
			throw error;
		}
	}

	private _disconnected = () => {
		const wallet = this._wallet;
		if (wallet) {
			wallet.off('disconnect', this._disconnected);

			this._wallet = null;
			this._publicKey = null;

			this.emit('error', new WalletDisconnectedError());
			this.emit('disconnect');
		}
	};
}
